CREATE TABLE `location` (
  `Name` varchar(10) PRIMARY KEY NOT NULL
);
  
CREATE TABLE `question` (
  `ID` int(11) AUTO_INCREMENT PRIMARY KEY NOT NULL,
  `Text` varchar(255) DEFAULT NULL
);

CREATE TABLE `answer` (
  `ID` int(11) AUTO_INCREMENT PRIMARY KEY NOT NULL,
