CREATE TABLE `Comment` (
  `ID` int(11) AUTO_INCREMENT PRIMARY KEY NOT NULL,
  `UserName` varchar(50) NOT NULL,
  `RecipeID` int(11) NOT NULL,
  `Text` varchar(255) NOT NULL,
  `StarRating` int(1) NOT NULL,
