  FOREIGN KEY (`RecipeID`) REFERENCES `Recipe`(ID)
);

CREATE TABLE `UserLocation` (
  `UserName` varchar(50) NOT NULL,
  `LocationName` varchar(10) NOT NULL,
  `DateTime` timestamp NOT NULL,
  PRIMARY KEY (`UserName`,`LocationName`),
  FOREIGN KEY (`UserName`) REFERENCES `User`(Name),
  FOREIGN KEY (`LocationName`) REFERENCES `location`(`name`)
);

CREATE TABLE `compentry` (
  `EntryID` int(11) AUTO_INCREMENT NOT NULL,
  `ClassName` varchar(50) NOT NULL,
  `SchoolName` varchar(50) NOT NULL,
  `EmailAddress` varchar(50) NOT NULL,
  `PhoneNo` varchar(15) NOT NULL,
  `QuestionID` int(11) NULL,
  `AnswerID` int(11) NULL,
  PRIMARY KEY (`EntryID`),
  FOREIGN KEY (`QuestionID`) REFERENCES `question` (`ID`),
  FOREIGN KEY (`AnswerID`) REFERENCES `answer` (`ID`)
);

CREATE TABLE `Schools` (
  `SchoolID` int(11) AUTO_INCREMENT NOT NULL,
  `SchoolName` varchar(255) NOT NULL,
  PRIMARY KEY (`SchoolID`)
);

