CREATE PROCEDURE  SignIn(
				  $UserName	varchar(50),
                  $UserPassword	varchar(50))
				  
	BEGIN	
    
			DECLARE lcSignInFail int;
    
			IF EXISTS
            (
			SELECT * from `user`
            WHERE $UserName = Name
            AND
            $UserPassword = Password
            )
            THEN
            SELECT * from `user`
            WHERE $UserName = Name
            AND
            $UserPassword = Password;
            
            ELSE
            SET lcSignInFail = 1;
            SELECT lcSignInFail;
            
            END IF;
			
            
END//

